require 'json'

PARENTAMENITIES.each do |parent_amenity|
  amenity = AmenityCategory.find_or_create_by!(name: parent_amenity)
  amenity_hash_values = CHILD_AMENITIES.select{|c| c["#{parent_amenity}"]}.first.values.flatten
  property_level_amenities = JSON[amenity_hash_values.first]
  room_level_amenities = JSON[amenity_hash_values.last]
  property_level_amenities.present? && property_level_amenities.each do |property_amenity|
    Amenity.find_or_create_by!(name: parent_amenity, is_property_level: true, amenity_category_id: amenity.id)
  end
  room_level_amenities.present? && room_level_amenities.each  do |room_amenity|
    Amenity.find_or_create_by!(name: parent_amenity, is_property_level: false, amenity_category_id: amenity.id)
  end
  
end

